package simplechat;// This file contains material supporting section 3.7 of the textbook:// "Object Oriented Software Engineering" and is issued under the open-source// license found at www.lloseng.comimport simplechat.client.ChatClient;import simplechat.common.ConsoleUI;import java.io.IOException;/** * This class constructs the UI for a chat simplechat.client.  It implements the * chat interface in order to activate the display() method. * Warning: Some of the code here is cloned in ServerConsole * * @author Fran&ccedil;ois B&eacute;langer * @author Dr Timothy C. Lethbridge * @author Dr Robert Lagani&egrave;re * @author Florent Hugouvieux * @version October 2020 */public class ClientConsole extends ConsoleUI{    /**     * The default port to connect on.     */    public static final int DEFAULT_PORT = 5555;    /**     * This method is responsible for the creation of the Client UI.     *     * @param args A [host, port] string array (optional).     */    public static void main(String[] args)    {        String host = "";        int port;        try        {            host = args[0];        }        catch (ArrayIndexOutOfBoundsException e)        {            host = "localhost";        }        try        {            port = Integer.parseInt(args[1]);        }        catch (ArrayIndexOutOfBoundsException e)        {            port = DEFAULT_PORT;        }        catch (NumberFormatException e)        {            throw new IllegalArgumentException("You must specify a integer for the port (second parameter)");        }        try        {            ClientConsole chat = new ClientConsole();            ChatClient client = new ChatClient(host, port, chat);            chat.setInputHandler(client::handleMessageFromClientUI);            chat.startQueryingInput();        }        catch (IOException e)        {            e.printStackTrace();        }    }    //Class methods ***************************************************    /**     * This method overrides the method in the ChatIF interface.  It     * displays a message onto the screen.     *     * @param message The string to be displayed.     */    public void display(String message)    {        System.out.println("> " + message);    }}//End of ConsoleChat class