// This file contains material supporting section 3.7 of the textbook:// "Object Oriented Software Engineering" and is issued under the open-source// license found at www.lloseng.com package simplechat.client;import ocsf.client.AbstractClient;import simplechat.commands.exceptions.CommandException;import simplechat.commands.parser.StringWalker;import simplechat.common.ChatIF;import java.io.IOException;/** * This class overrides some of the methods defined in the abstract * superclass in order to give more functionality to the simplechat.client. * * @author Dr Timothy C. Lethbridge * @author Dr Robert Lagani&egrave; * @author Fran&ccedil;ois B&eacute;langer * @version July 2000 */public class ChatClient extends AbstractClient{    public static final String COMMAND_PREFIX = "#";    //Instance variables **********************************************    /**     * The interface type variable.  It allows the implementation of     * the display method in the simplechat.client.     */    private final ChatIF clientUI;    private final ClientCommandsManager commandsManager = new ClientCommandsManager();    //Constructors ****************************************************    /**     * Constructs an instance of the chat simplechat.client.     *     * @param host     The server to connect to.     * @param port     The port number to connect on.     * @param clientUI The interface type variable.     */    public ChatClient(String host, int port, ChatIF clientUI)            throws IOException    {        super(host, port); //Call the superclass constructor        this.clientUI = clientUI;        openConnection();    }    //Instance methods ************************************************    /**     * This method handles all data that comes in from the server.     *     * @param msg The message from the server.     */    public void handleMessageFromServer(Object msg)    {        clientUI.display(msg.toString());    }    /**     * This method handles all data coming from the UI     *     * @param message The message from the UI.     */    public void handleMessageFromClientUI(String message)    {        if (message.startsWith(COMMAND_PREFIX))        {            executeCommand(message);        }        else        {            trySendToServer(message);        }    }    private void trySendToServer(String message)    {        try        {            sendToServer(message);        }        catch (IOException e)        {            clientUI.display("Could not send message to server. Terminating simplechat.client.");            quit();        }    }    private void executeCommand(String message)    {        try        {            StringWalker walker = new StringWalker(message);            walker.next(COMMAND_PREFIX.length());            commandsManager.handleCommandLine(walker, this);        }        catch (CommandException commandException)        {            clientUI.errorMessage(commandException.getMessage());        }    }    /**     * This method terminates the simplechat.client.     */    public void quit()    {        logOff();        System.exit(0);    }    public void logOff()    {        try        {            closeConnection();        }        catch (IOException e)        {            // Ignoring exception        }    }    public void changeHost(String newHost)    {        if (this.isConnected())        {            clientUI.errorMessage("You can't change the host while you're connected.");        }        else        {            setHost(newHost);            clientUI.infoMessage("Host is now : " + newHost);        }    }    public void changePort(int newPort)    {        if (this.isConnected())        {            clientUI.errorMessage("You can't change the port while you're connected.");        }        else        {            setPort(newPort);            clientUI.infoMessage("Port is now : " + newPort);        }    }    public void login()    {        if (this.isConnected())        {            clientUI.errorMessage("You're already connected.");        }        else        {            try            {                openConnection();                clientUI.infoMessage("Connected !");            }            catch (IOException e)            {                clientUI.errorMessage(String.format("Unable to connect to %s:%d\n", getHost(), getPort()));            }        }    }    @Override    protected void connectionClosed()    {        clientUI.infoMessage("Disconnected !");    }    @Override    protected void connectionException(Exception exception)    {        clientUI.infoMessage("Disconnected !");        System.exit(0);    }    public ChatIF getClientUI()    {        return clientUI;    }}//End of ChatClient class